lists.listFetches:
function (head, req) {
      log(['printing ', head.total_rows, ' rows']);
      start({
        'headers': {
          'Content-Type': 'application/json'
        }
      });
      send('[');
      var row;
      for (var i = 1; i < head.total_rows; i++) {
        row = getRow();
        send(toJSON(row.id) + ',');
      }
      row = getRow();
      send(toJSON(row.id));
      send(']');
    }

